mapper.select:
select id, copy_from
from core_chart_view
where copy_id = #{copyId}

SELECT `id`, `name`, `pid`, `org_id`, `level`, `node_type`, `type`, `canvas_style_data`, `component_data`, `mobile_layout`, `mobile_layout` as ext_flag, `status`, `self_watermark_status`, `sort`, `create_time`, `create_by`, `update_time`, `update_by`, `remark`, `source`, `delete_flag`, `delete_time`, `delete_by`, `version`, `content_id`, `check_version` FROM
snapshot_data_visualization_info dvi
data_visualization_info dvi
where dvi.delete_flag = 0 and dvi.id = #{dvId}
and dvi.type = #{dvType}

SELECT dvResource.id, dvResource.resource_id, dvResource.name, dvResource.ext_flag, dvResource.type, dvResource.creator, core_opt_recent.uid AS last_editor, core_opt_recent.time AS last_edit_time, ( CASE
WHEN core_store.resource_id IS NULL THEN 0 ELSE 1 END ) AS favorite FROM (SELECT core_dataset_group.id, core_dataset_group.id AS resource_id, core_dataset_group.NAME, 0 as ext_flag, 'dataset' AS type, core_dataset_group.create_by AS creator FROM core_dataset_group WHERE core_dataset_group.node_type = 'dataset' UNION ALL SELECT core_datasource.id, core_datasource.id AS resource_id, core_datasource.NAME, 0 as ext_flag, 'datasource' AS type, core_datasource.create_by AS creator FROM core_datasource WHERE core_datasource.type != 'folder' UNION ALL SELECT data_visualization_info.id, data_visualization_info.id AS resource_id, data_visualization_info.NAME, data_visualization_info.`mobile_layout` as ext_flag, ( CASE data_visualization_info.type WHEN 'dataV' THEN 'screen' ELSE 'panel' END ) AS type, data_visualization_info.create_by AS creator FROM data_visualization_info WHERE data_visualization_info.delete_flag = 0 AND node_type = 'leaf') dvResource LEFT JOIN core_store ON dvResource.id = core_store.resource_id AND core_store.uid = #{uid} INNER JOIN core_opt_recent ON dvResource.resource_id = core_opt_recent.resource_id AND core_opt_recent.uid = #{uid}
AND LOWER(dvResource.name) LIKE LOWER(CONCAT('%', #{keyword}, '%'))
AND dvResource.type = #{ew.type}
AND NOT EXISTS(select 1 from per_busi_resource community where core_opt_recent.resource_id = community.id)
order by core_opt_recent.time asc
